import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { projectAPI } from '../services/api';
import { toast } from 'react-toastify';
import { 
  Plus, 
  Users, 
  Calendar, 
  CheckCircle, 
  Clock, 
  AlertCircle,
  Loader,
  FolderOpen,
  TrendingUp,
  Shield,
  BarChart3,
  Activity,
  Edit,
  Trash2,
  Eye,
  Search,
  Filter,
  User,
  Building,
  ChevronLeft,
  ChevronRight,
  ToggleLeft,
  ToggleRight
} from 'lucide-react';

const Schedule = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [showPastDue, setShowPastDue] = useState(true);
  const [selectedClient, setSelectedClient] = useState('All Clients');
  const [selectedPeriod, setSelectedPeriod] = useState('Upcoming');

  // Mock team members data
  const teamMembers = [
    {
      id: 1,
      name: "Patrick",
      email: "patrick@taskmanagement.com",
      cards: 20,
      remainingHours: 18.0,
      tasks: [
        {
          id: 738,
          title: "Name and links are not populating",
          status: "Not Started",
          dueDate: "Oct 10",
          estimates: { est: "1h", act: "0h", rem: "1h" },
          assigned: "Patrick",
          priority: "high"
        },
        {
          id: 92,
          title: "Event Details View (Interface)",
          status: "Not Started", 
          dueDate: "Oct 15",
          estimates: { est: "2h", act: "0h", rem: "2h" },
          assigned: "Patrick",
          priority: "medium"
        }
      ]
    },
    {
      id: 2,
      name: "Osinachi",
      email: "osinachi@taskmanagement.com",
      cards: 0,
      remainingHours: 0.0,
      tasks: []
    },
    {
      id: 3,
      name: "Jone",
      email: "jone@taskmanagement.com",
      cards: 7,
      remainingHours: 15.0,
      tasks: [
        {
          id: 336,
          title: "Inquire About Partnership Form",
          status: "In Progress",
          dueDate: "Oct 21",
          estimates: { est: "0.5h", act: "2h", rem: "0h" },
          assigned: "Jone",
          priority: "low"
        },
        {
          id: 947,
          title: "Review Documentation",
          status: "Review",
          dueDate: "Oct 18",
          estimates: { est: "1h", act: "0h", rem: "1h" },
          assigned: "Jone",
          priority: "medium"
        }
      ]
    },
    {
      id: 4,
      name: "Gaurav N",
      email: "gaurav@taskmanagement.com",
      cards: 6,
      remainingHours: 20.0,
      tasks: [
        {
          id: 735,
          title: "Count everyone in the 'To' section as invited",
          status: "Escalated Review",
          dueDate: "Oct 10",
          estimates: { est: "9h", act: "8h", rem: "1h" },
          assigned: "Gaurav N",
          priority: "high"
        }
      ]
    }
  ];

  const getStatusColor = (status) => {
    const colors = {
      'Not Started': 'border-red-500 bg-red-50',
      'In Progress': 'border-yellow-500 bg-yellow-50',
      'Review': 'border-purple-500 bg-purple-50',
      'Escalated Review': 'border-red-500 bg-red-50',
      'Completed': 'border-green-500 bg-green-50'
    };
    return colors[status] || 'border-gray-500 bg-gray-50';
  };

  const getPriorityColor = (priority) => {
    const colors = {
      high: 'bg-red-100 text-red-800',
      medium: 'bg-yellow-100 text-yellow-800',
      low: 'bg-green-100 text-green-800'
    };
    return colors[priority] || 'bg-gray-100 text-gray-800';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="flex flex-col items-center space-y-4">
          <Loader className="h-8 w-8 animate-spin text-purple-600" />
          <p className="text-gray-600">Loading schedule...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Schedule</h1>
            <p className="text-gray-600 mt-1">Manage team member schedules and workload</p>
          </div>
        </div>
      </div>

      {/* Controls */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <select 
              value={selectedPeriod}
              onChange={(e) => setSelectedPeriod(e.target.value)}
              className="border border-gray-300 rounded-md px-3 py-2 text-sm focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              <option value="Upcoming">Upcoming</option>
              <option value="This Week">This Week</option>
              <option value="Next Week">Next Week</option>
            </select>

            <div className="flex items-center space-x-2 text-sm text-gray-600">
              <Calendar className="h-4 w-4" />
              <span>Oct 20 - Oct 26, 2025</span>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-600">Weekly Plan</span>
              <span className="text-sm text-gray-600">Team Members</span>
              <span className="text-sm text-gray-600">Outlook</span>
              <span className="text-sm text-gray-600">Time Entries</span>
            </div>

            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-600">Show past due cards</span>
              <button
                onClick={() => setShowPastDue(!showPastDue)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                  showPastDue ? 'bg-purple-600' : 'bg-gray-200'
                }`}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    showPastDue ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
            </div>

            <select 
              value={selectedClient}
              onChange={(e) => setSelectedClient(e.target.value)}
              className="border border-gray-300 rounded-md px-3 py-2 text-sm focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            >
              <option value="All Clients">All Clients</option>
              <option value="Uncharted">Uncharted</option>
              <option value="Other">Other</option>
            </select>
          </div>
        </div>
      </div>

      {/* Team Members Schedule */}
      <div className="flex space-x-6 overflow-x-auto pb-4">
        {teamMembers.map((member) => (
          <div key={member.id} className="flex-shrink-0 w-80">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
              <div className="mb-4">
                <h3 className="font-semibold text-lg text-gray-900">{member.name}</h3>
                <p className="text-sm text-gray-500">{member.email}</p>
                <div className="mt-2 text-sm text-gray-600">
                  <span className="font-medium">{member.cards} cards</span>
                  <span className="mx-2">•</span>
                  <span className="font-medium">{member.remainingHours}h remaining</span>
                </div>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {member.tasks.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    <div className="bg-gray-100 rounded-lg p-4">
                      <span className="text-sm">No cards this week</span>
                    </div>
                  </div>
                ) : (
                  member.tasks.map((task) => (
                    <div key={task.id} className={`${getStatusColor(task.status)} border-l-4 rounded-lg p-3 bg-white shadow-sm`}>
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex-1">
                          <h4 className="font-medium text-gray-900 text-sm">#{task.id} {task.title}</h4>
                          <p className="text-xs text-gray-500 mt-1">{task.status}</p>
                        </div>
                        <CheckCircle className="h-4 w-4 text-green-500" />
                      </div>

                      <div className="text-xs text-gray-500 mb-2">
                        Due: {task.dueDate}
                      </div>

                      <div className="text-xs text-gray-500 mb-2">
                        Est: {task.estimates.est} • Act: {task.estimates.act} • Rem: {task.estimates.rem}
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-xs text-gray-500">Assigned: {task.assigned}</span>
                        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(task.priority)}`}>
                          {task.priority}
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Floating Action Button */}
      <div className="fixed bottom-6 right-6">
        <button className="h-12 w-12 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-lg flex items-center justify-center transition-colors">
          <Calendar className="h-6 w-6" />
        </button>
      </div>
    </div>
  );
};

export default Schedule;
